import React, { Component } from "react";
import {Link} from "react-router-dom";
import axios from "axios";
import "./login.css";

class Login extends Component {
  // Setting the component's initial state
  state = {
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  };

  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    const { name, value } = event.target;

    // Updating the input's state
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();
    this.makeapicall()
    .then(response => {
      console.log(response);
      alert(`Welcome to our Yogi Place`);
      // this.setState({
      //   firstName: "",
      //   lastName: ""
      // })
      // this.props.history.push(`/feed/${response.data.email}`)
      this.props.history.push(`/feed/${response.data.data._id}`)
    })
    .catch(err => console.log(err));

  };



  makeapicall = () => {

    const getURl = endPoint => {
      if (process.env.MONGOLAB_URI) return endPoint;
      return `http://localhost:3001${endPoint}`;
    };

    const body = {
      first: this.state.firstName,
      last: this.state.lastName,
      email: this.state.email
    };

    // we are sending the body to the back end via json
    return axios
      .get(getURl("/api/users"), body)
  };

  render() {
    // Notice how each input has a `value`, `name`, and `onChange` prop
    return (
      <div className="container">

      <div className="col-md-12">
         <div className="card">
             <div className="card-body">
                 <h1 className="card-title">Yogi's Unite</h1>
                 <p>Welcome Login</p>
                 <form className="form">

                <div className="form-group"> 
                  <input
                  value={this.state.email}
                  name="email"
                  onChange={this.handleInputChange}
                  type="text"
                  placeholder="Email Address"
                  />
                  </div>

                  <div className="form-group"> 
                  <input
                  value={this.state.password}
                  name="password"
                  onChange={this.handleInputChange}
                  type="text"
                  placeholder="Password"
                  />
                  </div>

                  <button onClick={this.handleFormSubmit}>Login</button>
            </form>

             <div className="buttonLink">    
             <Link to="/signup">
                 <button className="btn btn-primary">Signup>></button>
             </Link>
             </div>
             </div>
             </div>
             </div>
             </div>

    );
  }
}

export default Login;
